import mongoose from 'mongoose';

// MongoDB connection string - prioritize environment variable first
let MONGODB_URI = process.env.MONGODB_URI || 'mongodb://localhost:27017/hospital-residents';

// If DATABASE_URL is set and looks like MongoDB, use it
if (process.env.DATABASE_URL && process.env.DATABASE_URL.includes('mongodb')) {
  MONGODB_URI = process.env.DATABASE_URL;
} else if (process.env.DATABASE_URL && process.env.DATABASE_URL.includes('postgresql')) {
  console.log('PostgreSQL DATABASE_URL detected, using MongoDB URI for hospital data');
}

let isConnected = false;

// Connect to MongoDB with retry logic
export async function connectDB() {
  if (isConnected) {
    return;
  }

  try {
    await mongoose.connect(MONGODB_URI, {
      serverSelectionTimeoutMS: 5000, // 5 second timeout
      socketTimeoutMS: 45000,
    });
    isConnected = true;
    console.log('Connected to MongoDB successfully at:', MONGODB_URI);
    await seedDatabase();
  } catch (error) {
    console.warn('MongoDB connection failed, using in-memory fallback:', (error as Error).message);
    console.log('To connect to MongoDB Atlas, set MONGODB_URI environment variable');
    // Initialize in-memory data for demo
    await initializeInMemoryData();
  }
}

// Seed database with sample data
async function seedDatabase() {
  try {
    const { ResidentModel, TeacherModel } = await import('./models');

    // Check if data already exists
    const residentCount = await ResidentModel.countDocuments();
    const teacherCount = await TeacherModel.countDocuments();

    if (residentCount === 0) {
      // Seed residents
      const residents = [
        {
          fullName: "Dr. Sarah Johnson",
          age: 28,
          gender: "Female",
          department: "Internal Medicine",
          startDate: new Date("2024-01-15"),
          status: "active"
        },
        {
          fullName: "Dr. Michael Chen",
          age: 29,
          gender: "Male", 
          department: "Surgery",
          startDate: new Date("2024-02-01"),
          status: "active"
        },
        {
          fullName: "Dr. Emily Rodriguez",
          age: 27,
          gender: "Female",
          department: "Pediatrics", 
          startDate: new Date("2024-03-10"),
          status: "active"
        }
      ];

      await ResidentModel.insertMany(residents);
      console.log('Sample residents data seeded successfully');
    }

    if (teacherCount === 0) {
      // Seed teachers
      const teachers = [
        {
          fullName: "Dr. Robert Williams",
          email: "r.williams@hospital.com",
          phone: "555-0101",
          department: "Internal Medicine",
          academicRank: "Professor",
          appointmentDate: new Date("2015-08-01"),
          status: "active"
        },
        {
          fullName: "Dr. Lisa Anderson",
          email: "l.anderson@hospital.com", 
          phone: "555-0102",
          department: "Surgery",
          academicRank: "Associate Professor",
          appointmentDate: new Date("2018-09-15"),
          status: "active"
        }
      ];

      await TeacherModel.insertMany(teachers);
      console.log('Sample teachers data seeded successfully');
    }
  } catch (error) {
    console.error('Error seeding database:', error);
  }
}

// Initialize in-memory data when MongoDB is not available
async function initializeInMemoryData() {
  console.log('MongoDB initialization failed, using in-memory storage');
  // The models will work with in-memory data for demo purposes
}

// Check if MongoDB is connected
export function isMongoConnected() {
  return isConnected && mongoose.connection.readyState === 1;
}

// Export mongoose for direct use if needed
export { mongoose };

server/demoAuth.ts.content:
import type { Express, RequestHandler } from "express";
import session from "express-session";
import MemoryStore from "memorystore";
import { storage } from "./storage";
import { DEMO_CREDENTIALS } from "@shared/schema";

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const MemoryStoreSession = MemoryStore(session);

  return session({
    secret: process.env.SESSION_SECRET || 'demo-secret-key-for-hospital-system',
    store: new MemoryStoreSession({
      checkPeriod: 86400000, // prune expired entries every 24h
    }),
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: false, // Set to false for development
      maxAge: sessionTtl,
    },
  });
}

export async function setupDemoAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());

  // Demo login endpoint
  app.post("/api/login", async (req, res) => {
    const { username, password } = req.body;

    // Check demo credentials
    const isAdmin = username === DEMO_CREDENTIALS.admin.username && 
                   password === DEMO_CREDENTIALS.admin.password;
    const isViewer = username === DEMO_CREDENTIALS.viewer.username && 
                    password === DEMO_CREDENTIALS.viewer.password;

    if (isAdmin || isViewer) {
      const credentials = isAdmin ? DEMO_CREDENTIALS.admin : DEMO_CREDENTIALS.viewer;

      // Store user in session
      (req.session as any).user = {
        claims: {
          sub: credentials.id,
          email: credentials.username,
          first_name: credentials.firstName,
          last_name: credentials.lastName,
        },
        expires_at: Math.floor(Date.now() / 1000) + (7 * 24 * 60 * 60), // 1 week from now
      };

      // Also ensure user exists in database
      await storage.upsertUser({
        _id: credentials.id,
        email: credentials.username,
        firstName: credentials.firstName,
        lastName: credentials.lastName,
        role: credentials.role,
      });

      res.json({ 
        success: true, 
        user: {
          _id: credentials.id,
          email: credentials.username,
          firstName: credentials.firstName,
          lastName: credentials.lastName,
          role: credentials.role
        }
      });
    } else {
      res.status(401).json({ message: "Invalid credentials" });
    }
  });

  // Demo logout endpoint
  app.get("/api/logout", (req, res) => {
    req.session.destroy(() => {
      res.json({ success: true });
    });
  });

  // Get current demo user
  app.get("/api/demo-info", (req, res) => {
    res.json({
      admin: {
        username: DEMO_CREDENTIALS.admin.username,
        password: DEMO_CREDENTIALS.admin.password,
      },
      viewer: {
        username: DEMO_CREDENTIALS.viewer.username,  
        password: DEMO_CREDENTIALS.viewer.password,
      }
    });
  });
}

export const isDemoAuthenticated: RequestHandler = async (req, res, next) => {
  const user = (req.session as any)?.user;

  if (!user || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now > user.expires_at) {
    return res.status(401).json({ message: "Session expired" });
  }

  // Add user to request for compatibility
  (req as any).user = user;
  return next();
};

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { connectDB } from "./db";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // Initialize database connection
  await connectDB();

  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    // reusePort: true,
  }, async () => {
    log(`serving on port ${port}`);

    // Setup vite after server is listening to avoid deadlock
    if (app.get("env") === "development") {
      try {
        await setupVite(app, server);
        log("Vite development server setup complete");
      } catch (error) {
        console.error("Vite setup failed:", error);
      }
    } else {
      serveStatic(app);
    }
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import MongoStore from "connect-mongo";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const mongoStore = MongoStore.create({
    mongoUrl: process.env.DATABASE_URL || 'mongodb://localhost:27017/hospital-residents',
    touchAfter: 24 * 3600, // lazy session update
  });

  return session({
    secret: process.env.SESSION_SECRET || 'demo-secret-key-for-hospital-system',
    store: mongoStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: false, // Set to false for development
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

