import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { Plus, Search, Grid3X3, List } from "lucide-react";
import ResidentCard from "@/components/residents/resident-card";
import ResidentDetails from "@/components/residents/resident-details";
import Sidebar from "@/components/layout/sidebar";
import { useAuth } from "@/hooks/useAuth";
import type { Resident } from "@shared/schema";

export default function Residents() {
  const { user } = useAuth();
  const [selectedResident, setSelectedResident] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [departmentFilter, setDepartmentFilter] = useState("all");
  const [viewMode, setViewMode] = useState<"grid" | "list">("list");

  const { data: residents = [], isLoading } = useQuery<Resident[]>({
    queryKey: ["/api/residents"],
  });

  const filteredResidents = residents.filter((resident) => {
    const matchesSearch = resident.fullName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         resident.department.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDepartment = departmentFilter === "all" || resident.department === departmentFilter;
    return matchesSearch && matchesDepartment;
  });

  const departments = Array.from(new Set(residents.map(r => r.department)));

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-50">
        <Sidebar />
        <div className="mr-64 p-6">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-slate-200 rounded w-64"></div>
            <div className="h-20 bg-slate-200 rounded"></div>
            <div className="space-y-4">
              {[...Array(3)].map((_, i) => (
                <div key={i} className="h-32 bg-slate-200 rounded"></div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50">
      <Sidebar />
      <div className="mr-64 p-6">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-slate-200 -m-6 mb-6">
        <div className="px-6 py-4">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-semibold text-slate-900">مدیریت ترینری</h1>
            {user?.role === 'admin' && (
              <Button 
                className="bg-hospital-green-600 hover:bg-hospital-green-700"
                data-testid="button-add-resident"
              >
                <Plus className="h-4 w-4 ml-2" />
                افزودن ترینری جدید
              </Button>
            )}
          </div>
        </div>
      </header>

      {/* Search and Filters */}
      <div className="mb-6 bg-white p-4 rounded-lg shadow-sm border border-slate-200">
        <div className="flex flex-wrap items-center justify-between gap-4">
          <div className="flex items-center space-x-4">
            <div className="relative">
              <Search className="h-4 w-4 absolute right-3 top-1/2 transform -translate-y-1/2 text-slate-400" />
              <Input
                type="text"
                placeholder="جستجو ترینری..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pr-10 w-64"
                data-testid="input-search-residents"
              />
            </div>
            <Select  value={departmentFilter} onValueChange={setDepartmentFilter}>
              <SelectTrigger className="w-48" data-testid="select-department-filter">
                <SelectValue placeholder="همه بخش‌ها" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">همه بخش‌ها</SelectItem>
                {departments.map((dept) => (
                  <SelectItem key={dept} value={dept}>{dept}</SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          {/* <div className="flex items-center space-x-2">
            <span className="text-sm text-slate-600">نمایش:</span>
            <Button
              variant={viewMode === "grid" ? "default" : "ghost"}
              size="sm"
              onClick={() => setViewMode("grid")}
              data-testid="button-grid-view"
            >
              <Grid3X3 className="h-4 w-4" />
            </Button>
            <Button
              variant={viewMode === "list" ? "default" : "ghost"}
              size="sm"
              onClick={() => setViewMode("list")}
              data-testid="button-list-view"
            >
              <List className="h-4 w-4" />
            </Button>
          </div> */}
        </div>
      </div>

      {/* Residents List */}
      <div className="grid gap-6">
        {filteredResidents.map((resident) => (
          <ResidentCard
            key={resident.id}
            resident={resident}
            onClick={() => setSelectedResident(resident.id)}
            data-testid={`card-resident-${resident.id}`}
          />
        ))}
        {filteredResidents.length === 0 && (
          <div className="text-center py-12 text-slate-500">
            <p>هیچ ترینری با این مشخصات یافت نشد.</p>
          </div>
        )}
      </div>

      {/* Selected Resident Details */}
      {selectedResident && (
        <ResidentDetails
          residentId={selectedResident}
          onClose={() => setSelectedResident(null)}
        />
      )}
      </div>
    </div>
  );
}

client/src/pages/setting.tsx.content:
// UserManagement.tsx
import { useEffect, useState } from "react";
import axios from "axios";
import { User } from "@shared/schema";
import Sidebar from "@/components/layout/sidebar";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { Plus, Search } from "lucide-react";
import { Input } from "@/components/ui/input";

export default function UserManagement() {
  const { toast } = useToast();
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [isUserDialogOpen, setIsUserDialogOpen] = useState(false);




  // دریافت کاربران از سرور
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const res = await axios.get<User[]>("/api/users");
      setUsers(res.data);
    } catch (err: any) {
      setError(err.message || "خطا در دریافت کاربران");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // حذف کاربر
  const deleteUser = async (id: string) => {
    if (!confirm("آیا مطمئن هستید که می‌خواهید این کاربر را حذف کنید؟")) return;
    try {
      await axios.delete(`/api/users/${id}`);
      setUsers((prev) => prev.filter((u) => u._id !== id));
      toast({ title: "موفقیت", description: "کاربر حذف شد" });
    } catch (err: any) {
      toast({ title: "خطا", description: "حذف کاربر با خطا مواجه شد", variant: "destructive" });
    }
  };

// فیلتر جستجو
const filteredUsers = users.filter(
  (u) =>
    u.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    u.email?.toLowerCase().includes(searchTerm.toLowerCase())
);


  return (
    <div className="min-h-screen bg-slate-50">
      <Sidebar />
      <div className="mr-64 p-6 space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold tracking-tight">مدیریت کاربران</h1>
            <p className="text-slate-600 dark:text-slate-400">مدیریت حساب‌های کاربران سیستم</p>
          </div>
          <Button
  onClick={() => setIsUserDialogOpen(true)}
  data-testid="button-add-user"
>
  <Plus className="h-4 w-4 mr-2" />
  افزودن کاربر جدید
</Button>

        </div>

        {/* Main Card */}
        <Card>
          <CardHeader>
            <CardTitle>لیست کاربران</CardTitle>
            <CardDescription>مجموع {filteredUsers.length} کاربر</CardDescription>
          </CardHeader>
          <CardContent>
            {/* Search */}
            <div className="mb-6">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-slate-400" />
                <Input
                  placeholder="جستجو بر اساس نام یا ایمیل..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>

            {/* Content */}
            {loading ? (
              <div className="flex justify-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-hospital-green-500"></div>
              </div>
            ) : error ? (
              <p className="text-red-600">{error}</p>
            ) : filteredUsers.length === 0 ? (
              <div className="text-center py-8 text-slate-500">
                {searchTerm ? "هیچ کاربری یافت نشد" : "هنوز کاربری اضافه نشده است"}
              </div>
            ) : (
              <div className="overflow-x-auto">
                <table className="w-full border-collapse rounded-lg overflow-hidden shadow-sm">
                  <thead className="bg-slate-100 text-slate-700">
                    <tr>
                      <th className="border p-2 text-center">نام</th>
                      <th className="border p-2 text-center">ایمیل</th>
                      <th className="border p-2 text-center">نقش</th>
                      <th className="border p-2 text-center">عملیات</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredUsers.map((user) => (
                      <tr key={user._id} className="text-center hover:bg-slate-50">
                      <td className="border p-2">{user.firstName ?? "-"}</td>
                      <td className="border p-2">{user.email ?? "-"}</td>
                      <td className="border p-2">{user.role ?? "-"}</td>

                        <td className="border p-2 flex items-center justify-center gap-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => alert("ویرایش کاربر هنوز آماده نیست")}
                          >
                            ویرایش
                          </Button>
                          <Button
                            variant="destructive"
                            size="sm"
                            onClick={() => deleteUser(user._id)}
                          >
                            حذف
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/teachers.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Plus, Search } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import TeacherTable from "@/components/teachers/teacher-table";
import TeacherFormDialog from "@/components/forms/teacher-form-dialog";
import { Teacher, InsertTeacher, insertTeacherSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import Sidebar from "@/components/layout/sidebar";

export default function Teachers() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState("");
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [editingTeacher, setEditingTeacher] = useState<Teacher | null>(null);

  const { data: teachers = [], isLoading, error } = useQuery<Teacher[]>({
    queryKey: ['/api/teachers'],
  });

  const createTeacherMutation = useMutation({
    mutationFn: async (teacherData: InsertTeacher) => {
      const response = await apiRequest('/api/teachers', {
        method: 'POST',
        body: JSON.stringify(teacherData),
        headers: { 'Content-Type': 'application/json' }
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/teachers'] });
      setIsAddDialogOpen(false);
      toast({
        title: "موفقیت",
        description: "استاد با موفقیت اضافه شد",
      });
    },
    onError: (error: any) => {
      toast({
        title: "خطا",
        description: error.message || "خطا در افزودن استاد",
        variant: "destructive",
      });
    },
  });

  const updateTeacherMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<InsertTeacher> }) => {
      const response = await apiRequest(`/api/teachers/${id}`, {
        method: 'PUT',
        body: JSON.stringify(data),
        headers: { 'Content-Type': 'application/json' }
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/teachers'] });
      setEditingTeacher(null);
      toast({
        title: "موفقیت",
        description: "استاد با موفقیت بروزرسانی شد",
      });
    },
    onError: (error: any) => {
      toast({
        title: "خطا",
        description: error.message || "خطا در بروزرسانی استاد",
        variant: "destructive",
      });
    },
  });

  const deleteTeacherMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest(`/api/teachers/${id}`, { method: 'DELETE' });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/teachers'] });
      toast({
        title: "موفقیت",
        description: "استاد با موفقیت حذف شد",
      });
    },
    onError: (error: any) => {
      toast({
        title: "خطا",
        description: error.message || "خطا در حذف استاد",
        variant: "destructive",
      });
    },
  });

  const filteredTeachers = teachers.filter((teacher: Teacher) =>
    teacher.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    teacher.department.toLowerCase().includes(searchTerm.toLowerCase()) ||
    teacher.subject.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleViewDetails = (teacher: Teacher) => {
    // Open teacher details modal
    console.log("View teacher details:", teacher);
  };

  const handleEdit = (teacher: Teacher) => {
    setEditingTeacher(teacher);
    setIsAddDialogOpen(true);
  };

  const handleDelete = (teacherId: string) => {
    if (window.confirm("آیا از حذف این استاد اطمینان دارید؟")) {
      deleteTeacherMutation.mutate(teacherId);
    }
  };

  const handleSubmit = (data: InsertTeacher) => {
    if (editingTeacher) {
      updateTeacherMutation.mutate({ id: editingTeacher._id, data });
    } else {
      createTeacherMutation.mutate(data);
    }
  };


  if (error) {
    return (
      <div className="min-h-screen bg-slate-50">
        <Sidebar />
        <div className="mr-64 p-6">
          <Card>
            <CardContent className="p-6">
              <p className="text-red-600">خطا در بارگذاری اطلاعات استادان</p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50">
      <Sidebar />
      <div className="mr-64 p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight" data-testid="heading-teachers">
            مدیریت استادان
          </h1>
          {/* <p className="text-slate-600 dark:text-slate-400">
            مدیریت اطلاعات استادان و هیئت علمی
          </p> */}
        </div>
        <Button 
          onClick={() => {
            setEditingTeacher(null);
            setIsAddDialogOpen(true);
          }}
          data-testid="button-add-teacher"
        >
          <Plus className="h-4 w-4 mr-2" />
          افزودن استاد جدید
        </Button>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>لیست استادان</CardTitle>
          <CardDescription>
            مجموع {filteredTeachers.length} استاد
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="mb-6">
            <div className="relative">
              <Search className="absolute left-3 top-3 h-4 w-4 text-slate-400" />
              <Input
                placeholder="جستجو بر اساس نام، بخش یا موضوع تدریس..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
                data-testid="input-search-teachers"
              />
            </div>
          </div>

          {isLoading ? (
            <div className="flex justify-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-hospital-green-500"></div>
            </div>
          ) : filteredTeachers.length > 0 ? (
            <TeacherTable
              teachers={filteredTeachers}
              onViewDetails={handleViewDetails}
              onEdit={handleEdit}
              onDelete={handleDelete}
            />
          ) : (
            <div className="text-center py-8 text-slate-500">
              {searchTerm ? "هیچ استادی با این مشخصات یافت نشد" : "هنوز استادی اضافه نشده است"}
            </div>
          )}
        </CardContent>
      </Card>

      <TeacherFormDialog
        isOpen={isAddDialogOpen}
        onClose={() => {
          setIsAddDialogOpen(false);
          setEditingTeacher(null);
        }}
        onSubmit={handleSubmit}
        title={editingTeacher ? "ویرایش معلومات استاد" : "افزودن استاد جدید"}
        defaultValues={editingTeacher || undefined}
        isSubmitting={createTeacherMutation.isPending || updateTeacherMutation.isPending}
      />
      </div>
    </div>
  );
}

client/src/components/forms/UserFormDial.content:
import * as React from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import axios from "axios";

interface UserFormDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: () => void;
}

export default function UserFormDialog({ isOpen, onClose, onSuccess }: UserFormDialogProps) {
  const { toast } = useToast();
  const [firstName, setFirstName] = useState("");
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("user"); // نقش پیش‌فرض
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post("/api/users", { firstName, email, role });
      toast({ title: "موفقیت", description: "کاربر جدید ثبت شد" });
      setFirstName("");
      setEmail("");
      setRole("user");
      onSuccess();
      onClose();
    } catch (err: any) {
      toast({ title: "خطا", description: err.message || "ثبت کاربر با خطا مواجه شد", variant: "destructive" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={(open) => !open && onClose()}>
      <DialogContent className="sm:max-w-lg">
        <DialogHeader>
          <DialogTitle>ثبت کاربر جدید</DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block mb-1">نام</label>
            <Input value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
          </div>
          <div>
            <label className="block mb-1">ایمیل</label>
            <Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div>
            <label className="block mb-1">نقش</label>
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="w-full border rounded-md p-2"
            >
              <option value="user">کاربر عادی</option>
              <option value="admin">مدیر</option>
            </select>
          </div>
          <DialogFooter>
            <Button type="submit" disabled={loading}>
              ثبت کاربر
            </Button>
            <Button variant="outline" onClick={onClose} disabled={loading}>
              لغو
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/forms/add-form-dia.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";

interface AddFormDialogProps {
  residentId: string;
  onClose: () => void;
  availableFormTypes: string[];
}

const FORM_TYPES = {
  J: 'Initial Assessment & Orientation',
  F: 'Mid-Training Evaluation',
  D: 'Clinical Skills Assessment',
  I: 'Research & Academic Progress',
  G: 'Communication Skills Review',
  E: 'Ethics & Professionalism',
  C: 'Case Presentation Evaluation',
  H: 'Hands-on Procedure Assessment',
  K: 'Final Competency Evaluation',
};

export default function AddFormDialog({ residentId, onClose, availableFormTypes }: AddFormDialogProps) {
  const [selectedFormType, setSelectedFormType] = useState<string>('');
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const createFormMutation = useMutation({
    mutationFn: async (formData: any) => {
      await apiRequest('POST', '/api/forms', formData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/residents', residentId, 'forms'] });
      toast({
        title: "Success",
        description: "Form created successfully.",
      });
      onClose();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create form. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleCreate = () => {
    if (!selectedFormType) {
      toast({
        title: "Error",
        description: "Please select a form type.",
        variant: "destructive",
      });
      return;
    }

    createFormMutation.mutate({
      residentId,
      formType: selectedFormType,
      formData: {},
      status: 'draft',
    });
  };

  return (
    <Dialog open={true} onOpenChange={onClose}>
      <DialogContent data-testid="dialog-add-form">
        <DialogHeader>
          <DialogTitle data-testid="text-add-form-title">Add New Form</DialogTitle>
        </DialogHeader>

        <div className="space-y-4 mt-4">
          <div>
            <Label htmlFor="formType">Form Type</Label>
            <Select value={selectedFormType} onValueChange={setSelectedFormType}>
              <SelectTrigger data-testid="select-form-type">
                <SelectValue placeholder="Select form type" />
              </SelectTrigger>
              <SelectContent>
                {availableFormTypes.map((formType) => (
                  <SelectItem key={formType} value={formType}>
                    Form {formType} - {FORM_TYPES[formType as keyof typeof FORM_TYPES]}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </div>

        <div className="flex items-center justify-end pt-4 border-t border-slate-200 mt-6 space-x-3">
          <Button variant="outline" onClick={onClose} data-testid="button-cancel-add-form">
            Cancel
          </Button>
          <Button
            onClick={handleCreate}
            disabled={createFormMutation.isPending}
            className="bg-hospital-green-600 hover:bg-hospital-green-700"
            data-testid="button-create-form"
          >
            {createFormMutation.isPending ? 'Creating...' : 'Create Form'}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

