                  <SelectItem value="1">Dr. Michael Chen</SelectItem>
                  <SelectItem value="2">Dr. Emily Rodriguez</SelectItem>
                  <SelectItem value="3">Dr. Sarah Wilson</SelectItem>
                </SelectContent>
              </Select>
