import { useQuery } from "@tanstack/react-query";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Clock, Plus, AlertCircle, Award } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import type { Resident, Form, DisciplinaryAction, Reward } from "@shared/schema";

interface ResidentCardProps {
  resident: Resident;
  onClick: () => void;
}

export default function ResidentCard({ resident, onClick }: ResidentCardProps) {
  const { user } = useAuth();

  const { data: forms = [] } = useQuery<Form[]>({
    queryKey: ["/api/residents", resident.id, "forms"],
  });

  const { data: disciplinaryActions = [] } = useQuery<DisciplinaryAction[]>({
    queryKey: ["/api/residents", resident.id, "disciplinary-actions"],
  });

  const { data: rewards = [] } = useQuery<Reward[]>({
    queryKey: ["/api/residents", resident.id, "rewards"],
  });

  const completedForms = forms.filter(form => form.status === 'completed').length;
  const totalForms = 9; // J, F, D, I, G, E, C, H, K

  return (
    <div className="bg-white rounded-lg shadow-sm border border-slate-200 hover:shadow-md transition-shadow cursor-pointer" onClick={onClick}>
      <div className="p-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Avatar className="h-12 w-12">
              <AvatarImage src={resident.profileImageUrl || ''} alt={resident.fullName} />
              <AvatarFallback>
                {resident.fullName.split(' ').map(n => n[0]).join('')}
              </AvatarFallback>
            </Avatar>
            <div>
              <h3 className="text-lg font-semibold text-slate-900" data-testid={`text-resident-name-${resident.id}`}>
                {resident.fullName}
              </h3>
              <p className="text-sm text-slate-600" data-testid={`text-resident-department-${resident.id}`}>
                {resident.department}
              </p>
              <p className="text-xs text-slate-500">
                Started: <span data-testid={`text-resident-start-date-${resident.id}`}>
                  {new Date(resident.startDate).toLocaleDateString()}
                </span>
              </p>
            </div>
          </div>
          <div className="flex items-center space-x-3">
            <div className="text-right">
              <p className="text-sm font-medium text-slate-900">Forms Completed</p>
              <p className="text-lg font-bold text-hospital-green-600" data-testid={`text-forms-completed-${resident.id}`}>
                {completedForms}/{totalForms}
              </p>
            </div>
            <div className="flex flex-col space-y-1">
              <Badge 
                variant="secondary" 
                className="bg-hospital-green-100 text-hospital-green-800"
                data-testid={`badge-status-${resident.id}`}
              >
                {resident.status}
              </Badge>
              {rewards.length > 0 && (
                <Badge 
                  variant="secondary" 
                  className="bg-yellow-100 text-yellow-800"
                  data-testid={`badge-rewards-${resident.id}`}
                >
                  {rewards.length} Rewards
                </Badge>
              )}
              {disciplinaryActions.length > 0 && (
                <Badge 
                  variant="secondary" 
                  className="bg-red-100 text-red-800"
                  data-testid={`badge-disciplinary-${resident.id}`}
                >
                  {disciplinaryActions.length} Disciplinary
                </Badge>
              )}
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="mt-4 flex items-center justify-between">
          <div className="flex space-x-2">
            {user?.role === 'admin' && (
              <>
                <Button
                  size="sm"
                  variant="outline"
                  className="text-xs"
                  onClick={(e) => {
                    e.stopPropagation();
                    // TODO: Implement add form functionality
                  }}
                  data-testid={`button-add-form-${resident.id}`}
                >
                  <Plus className="h-3 w-3 mr-1" />
                  Add Form
                </Button>
                <Button
                  size="sm"
                  variant="outline"
                  className="text-xs text-red-700 border-red-200 hover:bg-red-50"
                  onClick={(e) => {
                    e.stopPropagation();
                    // TODO: Implement record action functionality
                  }}
                  data-testid={`button-record-action-${resident.id}`}
                >
                  <AlertCircle className="h-3 w-3 mr-1" />
                  Record Action
                </Button>
              </>
            )}
            <Button
              size="sm"
              variant="outline"
              className="text-xs text-hospital-green-700 border-hospital-green-200 hover:bg-hospital-green-50"
              onClick={(e) => {
                e.stopPropagation();
                onClick();
              }}
              data-testid={`button-view-profile-${resident.id}`}
            >
              View Profile
            </Button>
          </div>
          <div className="flex items-center text-xs text-slate-500">
            <Clock className="h-3 w-3 mr-1" />
            Last updated {new Date(resident.updatedAt || resident.createdAt || Date.now()).toLocaleString()}
          </div>
        </div>
      </div>
    </div>
  );
}

import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { X, Plus, Edit, ExternalLink } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import FormModal from "@/components/forms/form-modal";
import type { Resident, Form, DisciplinaryAction, Reward } from "@shared/schema";

interface ResidentDetailsProps {
  residentId: string;
  onClose: () => void;
}

const FORM_TYPES = [
  { type: 'J', name: 'Initial Assessment & Orientation' },
  { type: 'F', name: 'Mid-Training Evaluation' },
  { type: 'D', name: 'Clinical Skills Assessment' },
  { type: 'I', name: 'Research & Academic Progress' },
  { type: 'G', name: 'Communication Skills Review' },
  { type: 'E', name: 'Ethics & Professionalism' },
  { type: 'C', name: 'Case Presentation Evaluation' },
  { type: 'H', name: 'Hands-on Procedure Assessment' },
  { type: 'K', name: 'Final Competency Evaluation' },
];

export default function ResidentDetails({ residentId, onClose }: ResidentDetailsProps) {
  const { user } = useAuth();
  const [selectedForm, setSelectedForm] = useState<Form | null>(null);

  const { data: resident } = useQuery<Resident>({
    queryKey: ["/api/residents", residentId],
  });

  const { data: forms = [] } = useQuery<Form[]>({
    queryKey: ["/api/residents", residentId, "forms"],
  });

  const { data: disciplinaryActions = [] } = useQuery<DisciplinaryAction[]>({
    queryKey: ["/api/residents", residentId, "disciplinary-actions"],
  });

  const { data: rewards = [] } = useQuery<Reward[]>({
    queryKey: ["/api/residents", residentId, "rewards"],
  });

  if (!resident) {
    return null;
  }

  const completedForms = forms.filter(form => form.status === 'completed');
  const pendingFormTypes = FORM_TYPES.filter(
    formType => !forms.some(form => form.formType === formType.type)
  );

  return (
    <div className="mt-8 bg-white rounded-lg shadow-sm border border-slate-200">
      <div className="border-b border-slate-200 px-6 py-4 flex items-center justify-between">
        <h3 className="text-lg font-semibold text-slate-900">Resident Details</h3>
        <Button
          variant="ghost"
          size="sm"
          onClick={onClose}
          data-testid="button-close-details"
        >
          <X className="h-4 w-4" />
        </Button>
      </div>

      <div className="p-6">
        <Tabs defaultValue="forms" className="w-full">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="forms" data-testid="tab-forms">
              Training Forms ({completedForms.length}/9)
            </TabsTrigger>
            <TabsTrigger value="actions" data-testid="tab-actions">
              Disciplinary Actions
            </TabsTrigger>
            <TabsTrigger value="rewards" data-testid="tab-rewards">
              Rewards & Achievements
            </TabsTrigger>
            <TabsTrigger value="profile" data-testid="tab-profile">
              Personal Info
            </TabsTrigger>
          </TabsList>

          <TabsContent value="forms" className="mt-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {/* Completed Forms */}
              {forms.map((form) => {
                const formType = FORM_TYPES.find(ft => ft.type === form.formType);
                return (
                  <div
                    key={form.id}
                    className="p-4 border border-slate-200 rounded-lg hover:shadow-sm cursor-pointer"
                    onClick={() => setSelectedForm(form)}
                    data-testid={`form-card-${form.formType}`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <h4 className="font-semibold text-slate-900">Form {form.formType}</h4>
                      <Badge
                        variant="secondary"
                        className={
                          form.status === 'completed'
                            ? "bg-hospital-green-100 text-hospital-green-800"
                            : "bg-yellow-100 text-yellow-800"
                        }
                      >
                        {form.status}
                      </Badge>
                    </div>
                    <p className="text-sm text-slate-600 mb-3">{formType?.name}</p>
                    <div className="flex items-center justify-between text-xs text-slate-500">
                      <span>
                        {form.completedAt 
                          ? `Completed: ${new Date(form.completedAt).toLocaleDateString()}`
                          : `Created: ${new Date(form.createdAt || Date.now()).toLocaleDateString()}`
                        }
                      </span>
                      {user?.role === 'admin' && (
                        <Button
                          size="sm"
                          variant="ghost"
                          className="text-hospital-green-600 hover:text-hospital-green-700 h-auto p-0"
                          data-testid={`button-edit-form-${form.formType}`}
                        >
                          <Edit className="h-3 w-3" />
                        </Button>
                      )}
                    </div>
                  </div>
                );
              })}

              {/* Add New Form Cards */}
              {pendingFormTypes.map((formType) => (
                <div
                  key={formType.type}
                  className="p-4 border-2 border-dashed border-slate-300 rounded-lg hover:border-hospital-green-300 cursor-pointer"
                  onClick={() => {
                    // TODO: Implement add new form functionality
                  }}
                  data-testid={`add-form-card-${formType.type}`}
                >
                  <div className="flex flex-col items-center justify-center py-4">
